(function(b){b.sections=b.sections||{};b.sections.frivolousPuzzles={name:"\u041d\u0435\u0441\u0435\u0440\u044c\u0435\u0437\u043d\u044b\u0435 \u0433\u043e\u043b\u043e\u0432\u043e\u043b\u043e\u043c\u043a\u0438",lang:"ru",questions:[{title:"\u0427\u0442\u043e \u0437\u0430 \u043d\u0430\u0434\u043f\u0438\u0441\u044c?",text:"\u0427\u0442\u043e \u0442\u0430\u043a\u043e\u0435: 86,smopuim ?",answer:"\u042d\u0442\u043e Windows'98 - \u0432\u0432\u0435p\u0445 \u043d\u043e\u0433\u0430\u043c\u0438!"},{title:"\u0417\u0435\u043b\u0435\u043d\u044b\u0435 \u0447\u0435\u043b\u043e\u0432\u0435\u0447\u043a\u0438",
text:"\u0427\u0442\u043e \u043d\u0430\u0434\u043e \u0434\u0435\u043b\u0430\u0442\u044c, \u0435\u0441\u043b\u0438 \u0432\u0438\u0434\u0438\u0448\u044c \u0437\u0435\u043b\u0435\u043d\u043e\u0433\u043e \u0447\u0435\u043b\u043e\u0432\u0435\u0447\u043a\u0430?",answer:"\u041f\u0435\u0440\u0435\u0445\u043e\u0434\u0438\u0442\u044c \u0434\u043e\u0440\u043e\u0433\u0443"},{title:"\u0421 \u0447\u0435\u0433\u043e \u043d\u0430\u0447\u0430\u0442\u044c",text:"\u041d\u0430 \u0441\u0442\u043e\u043b\u0435 \u043b\u0435\u0436\u0430\u0442 \u043b\u0438\u043d\u0435\u0439\u043a\u0430, \u043a\u0430\u0440\u0430\u043d\u0434\u0430\u0448, \u0446\u0438\u0440\u043a\u0443\u043b\u044c \u0438 \u0440\u0435\u0437\u0438\u043d\u043a\u0430. \u041d\u0430 \u043b\u0438\u0441\u0442\u0435 \u0431\u0443\u043c\u0430\u0433\u0438 \u043d\u0443\u0436\u043d\u043e \u043d\u0430\u0447\u0435\u0440\u0442\u0438\u0442\u044c \u043e\u043a\u0440\u0443\u0436\u043d\u043e\u0441\u0442\u044c. \u0421 \u0447\u0435\u0433\u043e \u043d\u0430\u0447\u0430\u0442\u044c?",
answer:"\u0421\u043d\u0430\u0447\u0430\u043b\u0430 \u043d\u0430\u0434\u043e \u0434\u043e\u0441\u0442\u0430\u0442\u044c \u043b\u0438\u0441\u0442 \u0431\u0443\u043c\u0430\u0433\u0438"},{title:"\u0421\u043b\u043e\u043c\u0430\u043d\u043d\u0430\u044f \u043d\u043e\u0433\u0430",text:"\u041c\u0430\u043b\u044c\u0447\u0438\u043a \u0443\u043f\u0430\u043b \u0441 4 \u0441\u0442\u0443\u043f\u0435\u043d\u0435\u043a \u0438 \u0441\u043b\u043e\u043c\u0430\u043b \u043d\u043e\u0433\u0443. \u0421\u043a\u043e\u043b\u044c\u043a\u043e \u043d\u043e\u0433 \u0441\u043b\u043e\u043c\u0430\u0435\u0442 \u043c\u0430\u043b\u044c\u0447\u0438\u043a, \u0435\u0441\u043b\u0438 \u0443\u043f\u0430\u0434\u0435\u0442 \u0441 40 \u0441\u0442\u0443\u043f\u0435\u043d\u0435\u043a?",
answer:"\u0412\u0441\u0435\u0433\u043e \u043e\u0434\u043d\u0443, \u0442.\u043a. \u0432\u0442\u043e\u0440\u0430\u044f \u0443 \u043d\u0435\u0433\u043e \u0443\u0436\u0435 \u0441\u043b\u043e\u043c\u0430\u043d\u0430, \u043b\u0438\u0431\u043e \u0431\u043e\u043b\u044c\u0448\u0435 \u043d\u0438 \u043e\u0434\u043d\u043e\u0439, \u0435\u0441\u043b\u0438 \u043f\u043e\u0432\u0435\u0437\u0451\u0442"},{title:"\u041a\u043e\u043d\u0434\u0440\u0430\u0442",text:'\u0428\u0435\u043b \u041a\u043e\u043d\u0434\u0440\u0430\u0442 \u0432 \u041b\u0435\u043d\u0438\u043d\u0433\u0440\u0430\u0434,<br>\u0410 \u043d\u0430\u0432\u0441\u0442\u0440\u0435\u0447\u0443 - \u0434\u0432\u0435\u043d\u0430\u0434\u0446\u0430\u0442\u044c \u0440\u0435\u0431\u044f\u0442,<br>\u0423 \u043a\u0430\u0436\u0434\u043e\u0433\u043e \u043f\u043e \u0442\u0440\u0438 \u043b\u0443\u043a\u043e\u0448\u043a\u0430,<br>\u0412 \u043a\u0430\u0436\u0434\u043e\u043c \u043b\u0443\u043a\u043e\u0448\u043a\u0435 - \u043a\u043e\u0448\u043a\u0430,<br>\u0423 \u043a\u0430\u0436\u0434\u043e\u0439 \u043a\u043e\u0448\u043a\u0438 - \u0434\u0432\u0435\u043d\u0430\u0434\u0446\u0430\u0442\u044c \u043a\u043e\u0442\u044f\u0442,<br>\u0423 \u043a\u0430\u0436\u0434\u043e\u0433\u043e \u043a\u043e\u0442\u0435\u043d\u043a\u0430 \u0432 \u0437\u0443\u0431\u0430\u0445 \u043f\u043e \u0447\u0435\u0442\u044b\u0440\u0435 \u043c\u044b\u0448\u043e\u043d\u043a\u0430.<br>\u0418 \u0437\u0430\u0434\u0443\u043c\u0430\u043b\u0441\u044f \u0441\u0442\u0430\u0440\u044b\u0439 \u041a\u043e\u043d\u0434\u0440\u0430\u0442:<br>"\u0421\u043a\u043e\u043b\u044c\u043a\u043e \u043c\u044b\u0448\u0430\u0442 \u0438 \u043a\u043e\u0442\u044f\u0442<br>\u0420\u0435\u0431\u044f\u0442\u0430 \u043d\u0435\u0441\u0443\u0442 \u0432 \u041b\u0435\u043d\u0438\u043d\u0433\u0440\u0430\u0434?"',
answer:"\u0413\u043b\u0443\u043f\u044b\u0439, \u0433\u043b\u0443\u043f\u044b\u0439 \u041a\u043e\u043d\u0434\u0440\u0430\u0442!<br>\u041e\u043d \u043e\u0434\u0438\u043d \u0438 \u0448\u0430\u0433\u0430\u043b \u0432 \u041b\u0435\u043d\u0438\u043d\u0433\u0440\u0430\u0434.<br>\u0410 \u0440\u0435\u0431\u044f\u0442\u0430 \u0441 \u043b\u0443\u043a\u043e\u0448\u043a\u0430\u043c\u0438,<br>\u0421 \u043c\u044b\u0448\u043a\u0430\u043c\u0438 \u0438 \u043a\u043e\u0448\u043a\u0430\u043c\u0438<br>\u0428\u043b\u0438 \u043d\u0430\u0432\u0441\u0442\u0440\u0435\u0447\u0443 \u0435\u043c\u0443 - \u0432 \u041a\u043e\u0441\u0442\u0440\u043e\u043c\u0443"},
{title:"\u0421\u0442\u0440\u0430\u043d\u043d\u043e\u0435 \u0441\u043e\u0437\u0434\u0430\u043d\u0438\u0435",text:"\u0412\u043e\u0437\u043c\u043e\u0436\u043d\u043e \u043b\u0438 \u0442\u0430\u043a\u043e\u0435: \u0434\u0432\u0435 \u0433\u043e\u043b\u043e\u0432\u044b, \u0434\u0432\u0435 \u0440\u0443\u043a\u0438 \u0438 \u0448\u0435\u0441\u0442\u044c \u043d\u043e\u0433, \u0430 \u0432 \u0445\u043e\u0434\u044c\u0431\u0435 \u0442\u043e\u043b\u044c\u043a\u043e \u0447\u0435\u0442\u044b\u0440\u0435?",answer:"\u0414\u0430, \u044d\u0442\u043e \u0432\u0441\u0430\u0434\u043d\u0438\u043a \u043d\u0430 \u043b\u043e\u0448\u0430\u0434\u0438"},
{title:"\u0424\u0438\u0442\u043e-\u043f\u043b\u0430\u0442\u044c\u0435",text:"\u041a\u043e\u0433\u0434\u0430 \u043f\u043b\u0430\u0442\u044c\u044f \u0431\u044b\u0432\u0430\u044e\u0442 \u0438\u0437 \u0442\u0440\u0430\u0432\u044b?",answer:'\u041a\u043e\u0433\u0434\u0430 \u043e\u043d\u0438 "\u0438\u0437 \u043c\u044f\u0442\u044b"'},{title:"\u0417\u0430\u0434\u0430\u0447\u0430 \u043f\u0440\u043e \u0447\u0443\u043a\u0447\u0443",text:"\u0414\u0430\u043d \u0440\u044f\u0434: 10-9-8-7-6- -4-3-2-1-0.<br>\u0412\u043e\u043f\u0440\u043e\u0441: \u041f\u043e\u0447\u0435\u043cy \u0447y\u043a\u0447\u0430 \u043d\u0435 \u0445\u043e\u0447\u0435\u0442 \u0435\u0441\u0442\u044c p\u044b\u0431y? ",
answer:'\u0410 \u043f\u044f\u0442\u0438-\u0442\u043e \u043d\u0435\u0442\u0443! (\u043fp\u043e\u0438\u0437\u043d\u043e\u0441\u0438\u0442\u0441\u044f \u043a\u0430\u043a "\u0430\u043f\u043f\u0435\u0442\u0438\u0442\u0430 \u043d\u0435\u0442y")'},{title:"\u0420\u043e\u0441\u0441\u0438\u044f \u0438 \u0424\u0440\u0430\u043d\u0446\u0438\u044f",text:"\u0427\u0442\u043e \u0432 \u0420\u043e\u0441\u0441\u0438\u0438 \u043d\u0430 \u043f\u0435\u0440\u0432\u043e\u043c \u043c\u0435\u0441\u0442\u0435, \u0430 \u0432\u043e \u0424\u0440\u0430\u043d\u0446\u0438\u0438 \u043d\u0430 \u0432\u0442\u043e\u0440\u043e\u043c?",
answer:'\u0411\u0443\u043a\u0432\u0430 "\u0420"'},{title:"\u041d\u0435\u0446\u0435\u043d\u0437\u0443\u0440\u043d\u0430\u044f",text:'\u041d\u0430 \u0431\u0443\u043a\u0432\u0443 "\u0425" \u043d\u0430\u0437\u044b\u0432\u0430\u0435\u0442\u0441\u044f, "\u041f" \u0443\u0432\u0438\u0434\u0438\u0442 \u043f\u043e\u0434\u044b\u043c\u0430\u0435\u0442\u0441\u044f',answer:"\u0425\u043e\u0431\u043e\u0442 \u043f\u0438\u0449\u0443 \u0431\u0435\u0440\u0435\u0442"},{title:"\u0417\u043e\u043d\u0442\u0438\u043a",text:"\u0412 \u043a\u0430\u043a\u043e\u043c \u0441\u043b\u0443\u0447\u0430\u0435 \u0448\u0435\u0441\u0442\u044c \u0434\u0435\u0442\u0435\u0439, \u0434\u0432\u0435 \u0441\u043e\u0431\u0430\u043a\u0438, \u0447\u0435\u0442\u0432\u0435\u0440\u043e \u0432\u0437\u0440\u043e\u0441\u043b\u044b\u0445, \u0437\u0430\u0431\u0440\u0430\u0432\u0448\u0438\u0441\u044c \u043f\u043e\u0434 \u043e\u0434\u0438\u043d \u0437\u043e\u043d\u0442\u0438\u043a, \u043d\u0435 \u043d\u0430\u043c\u043e\u043a\u043d\u0443\u0442?",
answer:"\u0415\u0441\u043b\u0438 \u043d\u0435 \u0431\u0443\u0434\u0435\u0442 \u0434\u043e\u0436\u0434\u044f"},{title:"\u0422\u0443\u0434\u0430-\u0441\u044e\u0434\u0430",text:"\u0422\u0443\u0434\u0430-\u0441\u044e\u0434\u0430, \u043e\u0431\u0440\u0430\u0442\u043d\u043e, \u0442\u0435\u0431\u0435 \u0438 \u043c\u043d\u0435 \u043f\u0440\u0438\u044f\u0442\u043d\u043e",answer:"\u041a\u0430\u0447\u0435\u043b\u0438"},{title:"\u041a\u0430\u043c\u043d\u0438",text:"\u041a\u0430\u043a\u0438\u0445 \u043a\u0430\u043c\u043d\u0435\u0439 \u043d\u0435\u0442 \u0432 \u043c\u043e\u0440\u0435?",
answer:"\u0421\u0443\u0445\u0438\u0445"},{title:"\u041a\u0442\u043e \u044d\u0442\u043e?",text:"\u041e\u0434\u0438\u043d \u0433\u043b\u0430\u0437, \u043e\u0434\u0438\u043d \u0440\u043e\u0433, \u043d\u043e \u043d\u0435 \u043d\u043e\u0441\u043e\u0440\u043e\u0433?",answer:"\u041a\u043e\u0440\u043e\u0432\u0430 \u0438\u0437-\u0437\u0430 \u0443\u0433\u043b\u0430 \u0432\u044b\u0433\u043b\u044f\u0434\u044b\u0432\u0430\u0435\u0442"},{title:"\u042f\u0431\u043b\u043e\u043a\u043e",text:"\u0427\u0442\u043e \u043c\u043e\u0436\u0435\u0442 \u0431\u044b\u0442\u044c \u0445\u0443\u0436\u0435, \u0447\u0435\u043c \u043d\u0430\u0439\u0442\u0438 \u0432 \u044f\u0431\u043b\u043e\u043a\u0435 \u0447\u0435\u0440\u0432\u044f\u043a\u0430?",
answer:"\u041d\u0430\u0439\u0442\u0438 \u0432 \u044d\u0442\u043e\u043c \u0436\u0435 \u044f\u0431\u043b\u043e\u043a\u0435 \u043f\u043e\u043b\u043e\u0432\u0438\u043d\u043a\u0443 \u0447\u0435\u0440\u0432\u044f\u043a\u0430"},{title:"13 \u0440\u0430\u0437",text:"\u041a\u0430\u043a\u043e\u0435 \u0432\u0440\u0435\u043c\u044f \u043d\u0430\u0441\u0442\u0443\u043f\u0430\u0435\u0442, \u043a\u043e\u0433\u0434\u0430 \u0447\u0430\u0441\u044b \u0431\u044c\u044e\u0442 13 \u0440\u0430\u0437?",answer:"\u041d\u0430\u0441\u0442\u0443\u043f\u0430\u0435\u0442 \u0432\u0440\u0435\u043c\u044f \u0447\u0438\u043d\u0438\u0442\u044c \u0447\u0430\u0441\u044b"},
{title:"\u041f\u0435\u0441\u0441\u0438\u043c\u0438\u0441\u0442, \u0440\u0435\u0430\u043b\u0438\u0441\u0442, \u043e\u043f\u0442\u0438\u043c\u0438\u0441\u0442",text:"\u0423\u0437\u043d\u0430\u0432 \u043e\u0442 \u0432\u0440\u0430\u0447\u0430, \u0447\u0442\u043e \u0436\u0438\u0442\u044c \u0438\u043c \u043e\u0441\u0442\u0430\u043b\u043e\u0441\u044c \u0441\u043e\u0432\u0441\u0435\u043c \u043d\u0435\u0434\u043e\u043b\u0433\u043e: \u043f\u0435\u0441\u0441\u0438\u043c\u0438\u0441\u0442 \u043d\u0430\u043f\u0438\u0432\u0430\u0435\u0442\u0441\u044f \u0441 \u0433\u043e\u0440\u044f, \u0440\u0435\u0430\u043b\u0438\u0441\u0442 \u043f\u0438\u0448\u0435\u0442 \u0437\u0430\u0432\u0435\u0449\u0430\u043d\u0438\u0435, \u0430 \u0447\u0442\u043e \u0436\u0435 \u0434\u0435\u043b\u0430\u0435\u0442 \u043e\u043f\u0442\u0438\u043c\u0438\u0441\u0442 \u0432 \u044d\u0442\u043e\u0439 \u0441\u0438\u0442\u0443\u0430\u0446\u0438\u0438?",
answer:"\u0418\u0434\u0435\u0442 \u043a \u0434\u0440\u0443\u0433\u043e\u043c\u0443 \u0432\u0440\u0430\u0447\u0443 - \u043c\u043e\u0436\u0435\u0442, \u044d\u0442\u043e\u0442 \u043e\u0448\u0438\u0431\u0441\u044f"},{title:"\u0416\u0435\u043d\u0441\u043a\u0430\u044f \u0441\u0443\u043c\u043e\u0447\u043a\u0430",text:"\u0427\u0435\u0433\u043e \u043d\u0435\u0442 \u0432 \u0436\u0435\u043d\u0441\u043a\u043e\u0439 \u0441\u0443\u043c\u043e\u0447\u043a\u0435?",answer:"\u041f\u043e\u0440\u044f\u0434\u043a\u0430"},
{title:"\u041a\u0430\u0441\u0442\u0440\u044e\u043b\u044f",text:"\u0427\u0442\u043e \u043d\u0435 \u0432\u043b\u0435\u0437\u0435\u0442 \u0434\u0430\u0436\u0435 \u0432 \u0441\u0430\u043c\u0443\u044e \u0431\u043e\u043b\u044c\u0448\u0443\u044e \u043a\u0430\u0441\u0442\u0440\u044e\u043b\u044e?",answer:"\u041a\u0440\u044b\u0448\u043a\u0430 \u043e\u0442 \u043d\u0435\u0435"},{title:"\u0417\u0430\u0433\u0430\u0434\u043a\u0430",text:"\u0412\u044b\u0448\u0435 \u043a\u043e\u043b\u0435\u043d\u0430, \u043d\u0438\u0436\u0435 \u043f\u0443\u043f\u043a\u0430, \u0434\u044b\u0440\u043a\u0430 \u0442\u0430\u043a\u0430\u044f, \u0447\u0442\u043e \u0432\u043b\u0435\u0437\u0435\u0442 \u0440\u0443\u043a\u0430.<br>\u0427\u0442\u043e \u044d\u0442\u043e?",
answer:"\u041a\u0430\u0440\u043c\u0430\u043d"},{title:"\u0411\u043e\u043b\u0442\u0443\u0448\u043a\u0430",text:"\u0412 \u043a\u0430\u043a\u043e\u043c \u043c\u0435\u0441\u044f\u0446\u0435 \u0431\u043e\u043b\u0442\u043b\u0438\u0432\u0430\u044f \u0421\u0432\u0435\u0442\u043e\u0447\u043a\u0430 \u0433\u043e\u0432\u043e\u0440\u0438\u0442 \u043c\u0435\u043d\u044c\u0448\u0435 \u0432\u0441\u0435\u0433\u043e?",answer:"\u0412 \u0444\u0435\u0432\u0440\u0430\u043b\u0435, \u0442\u0430\u043a \u043a\u0430\u043a \u044d\u0442\u043e \u0441\u0430\u043c\u044b\u0439 \u043a\u043e\u0440\u043e\u0442\u043a\u0438\u0439 \u043c\u0435\u0441\u044f\u0446"},
{title:"\u0417\u0430\u0433\u0430\u0434\u043a\u0430 \u043f\u0440\u043e \u043a\u0430\u0437\u043d\u044c \u0443\u0431\u0438\u0439\u0446\u044b",text:"\u0423\u0431\u0438\u0439\u0446\u0430 \u043f\u0440\u0438\u0433\u043e\u0432\u043e\u0440\u0435\u043d \u043a \u0441\u043c\u0435\u0440\u0442\u043d\u043e\u0439 \u043a\u0430\u0437\u043d\u0438. \u041e\u043d \u0434\u043e\u043b\u0436\u0435\u043d \u0432\u044b\u0431\u0440\u0430\u0442\u044c \u043e\u0434\u043d\u0443 \u0438\u0437 \u0442\u0440\u0435\u0445 \u043a\u043e\u043c\u043d\u0430\u0442: \u0432 \u043f\u0435\u0440\u0432\u043e\u0439 \u0431\u0443\u0448\u0443\u044e\u0442 \u043f\u043e\u0436\u0430\u0440\u044b, \u0432\u0442\u043e\u0440\u0430\u044f \u043f\u043e\u043b\u043d\u0430 \u0443\u0431\u0438\u0439\u0446 \u0441 \u0437\u0430\u0440\u044f\u0436\u0435\u043d\u043d\u044b\u043c\u0438 \u0440\u0443\u0436\u044c\u044f\u043c\u0438, \u0430 \u0432 \u0442\u0440\u0435\u0442\u044c\u0435\u0439 \u043c\u043d\u043e\u0433\u043e \u043b\u044c\u0432\u043e\u0432, \u043a\u043e\u0442\u043e\u0440\u044b\u0435 \u043d\u0435 \u0435\u043b\u0438 3 \u0433\u043e\u0434\u0430.<br>\u0412\u043e\u043f\u0440\u043e\u0441: \u043a\u0430\u043a\u0430\u044f \u043a\u043e\u043c\u043d\u0430\u0442\u0430 \u0431\u0443\u0434\u0435\u0442 \u0441\u0430\u043c\u043e\u0439 \u0431\u0435\u0437\u043e\u043f\u0430\u0441\u043d\u043e\u0439?",
answer:"\u041a\u043e\u043c\u043d\u0430\u0442\u0430 \u0441\u043e \u043b\u044c\u0432\u0430\u043c\u0438, \u043f\u043e\u0441\u043a\u043e\u043b\u044c\u043a\u0443 \u0432\u0441\u0435 \u043e\u043d\u0438 \u0443\u0436\u0435 \u0434\u0430\u0432\u043d\u043e \u043c\u0435\u0440\u0442\u0432\u044b"},{title:"\u0421\u0442\u0430\u044f",text:"\u041b\u0435\u0442\u0435\u043b\u0430 \u0441\u0442\u0430\u044f, \u0441\u043e\u0432\u0441\u0435\u043c \u043d\u0435\u0431\u043e\u043b\u044c\u0448\u0430\u044f. \u0427\u0442\u043e \u044d\u0442\u043e \u0431\u044b\u043b\u0438 \u0437\u0430 \u043f\u0442\u0438\u0446\u044b \u0438 \u0441\u043a\u043e\u043b\u044c\u043a\u043e \u0438\u0445 \u0431\u044b\u043b\u043e?",
answer:'\u0415\u0441\u043b\u0438 \u043f\u0440\u043e\u0438\u0437\u043d\u0435\u0441\u0442\u0438 \u044d\u0442\u0443 \u0437\u0430\u0433\u0430\u0434\u043a\u0443 \u0432\u0441\u043b\u0443\u0445, \u0442\u043e \u0441\u043b\u043e\u0432\u043e "\u0441\u043e\u0432\u0441\u0435\u043c" \u0437\u0432\u0443\u0447\u0438\u0442 \u043a\u0430\u043a "\u0441\u043e\u0432 \u0441\u0435\u043c\u044c", \u0442.\u0435. \u043e\u0442\u0432\u0435\u0442 - 7 \u0441\u043e\u0432'}]}})(window);
